            return this.monto;
        }

        public virtual void setIdSedeEntradaTarifa(int idSedeEntradaTarifa)
        {
            this.idSedeEntradaTarifa = idSedeEntradaTarifa;
        }

        private void setSede(Sede sede)
        {
            this.m_Sede = sede;
        }

    }
